# Generic motion controller definition
# All controller plugins inherit features from this one

from __future__ import absolute_import
from __future__ import print_function

from CNC import CNC, WCS
import time
import re

STATUSPAT = re.compile(r"^<(\w*?),MPos:([+\-]?\d*\.\d*),([+\-]?\d*\.\d*),([+\-]?\d*\.\d*),WPos:([+\-]?\d*\.\d*),([+\-]?\d*\.\d*),([+\-]?\d*\.\d*),?(.*)>$")
POSPAT	  = re.compile(r"^\[(...):([+\-]?\d*\.\d*),([+\-]?\d*\.\d*),([+\-]?\d*\.\d*):?(\d*)\]$")
TLOPAT	  = re.compile(r"^\[(...):([+\-]?\d*\.\d*)\]$")
DOLLARPAT = re.compile(r"^\[G\d* .*\]$")
SPLITPAT  = re.compile(r"[:,]")
VARPAT    = re.compile(r"^\$(\d+)=(\d*\.?\d*) *\(?.*")


class _GenericController:
	def test(self):
		print("test supergen")

	def executeCommand(self, oline, line, cmd):
		return False

	def hardResetPre(self):
		pass

	def hardResetAfter(self):
		pass

	def viewStartup(self):
		pass

	def checkGcode(self):
		pass

	def viewSettings(self):
		pass

	def grblRestoreSettings(self):
		pass

	def grblRestoreWCS(self):
		pass

	def grblRestoreAll(self):
		pass

	def purgeControllerExtra(self):
		pass

	def overrideSet(self):
		pass

	def hardReset(self):
		self.main.busy()
		if self.main.serial is not None:
			self.hardResetPre()
			self.main.openClose()
			self.hardResetAfter()
		self.main.openClose()
		self.main.stopProbe()
		self.main._alarm = False
		CNC.vars["_OvChanged"] = True	# force a feed change if any
		self.main.notBusy()

	#----------------------------------------------------------------------
	def softReset(self, clearAlarm=True):
		if self.main.serial:
			self.main.serial_write(b"\030")
		self.main.stopProbe()
		if clearAlarm: self.main._alarm = False
		CNC.vars["_OvChanged"] = True	# force a feed change if any

	#----------------------------------------------------------------------
	def unlock(self, clearAlarm=True):
		if clearAlarm: self.main._alarm = False
		self.main.sendGCode("$X")

	#----------------------------------------------------------------------
	def home(self, event=None):
		self.main._alarm = False
		self.main.sendGCode("$H")

	def viewStatusReport(self):
		self.main.serial_write(b"?")
		self.main.sio_status = True

	def viewParameters(self):
		self.main.sendGCode("$#")

	def viewState(self): #Maybe rename to viewParserState() ???
		self.main.sendGCode("$G")

	#----------------------------------------------------------------------
	def jog(self, dir):
		#print("jog",dir)
		self.main.sendGCode("G91G0%s"%(dir))
		self.main.sendGCode("G90")

	#----------------------------------------------------------------------
	def goto(self, x=None, y=None, z=None):
		cmd = "G90G0"
		if x is not None: cmd += "X%g"%(x)
		if y is not None: cmd += "Y%g"%(y)
		if z is not None: cmd += "Z%g"%(z)
		self.main.sendGCode("%s"%(cmd))

	#----------------------------------------------------------------------
	def _wcsSet(self, x, y, z):
		#global wcsvar
		#p = wcsvar.get()
		p = WCS.index(CNC.vars["WCS"])
		if p<6:
			cmd = "G10L20P%d"%(p+1)
		elif p==6:
			cmd = "G28.1"
		elif p==7:
			cmd = "G30.1"
		elif p==8:
			cmd = "G92"

		pos = ""
		if x is not None and abs(float(x))<10000.0: pos += "X"+str(x)
		if y is not None and abs(float(y))<10000.0: pos += "Y"+str(y)
		if z is not None and abs(float(z))<10000.0: pos += "Z"+str(z)
		cmd += pos
		self.main.sendGCode(cmd)
		self.viewParameters()
		self.main.event_generate("<<Status>>",
			data=(_("Set workspace %s to %s")%(WCS[p],pos)))
			#data=(_("Set workspace %s to %s")%(WCS[p],pos)))
		self.main.event_generate("<<CanvasFocus>>")

	#----------------------------------------------------------------------
	def feedHold(self, event=None):
		if event is not None and not self.main.acceptKey(True): return
		if self.main.serial is None: return
		self.main.serial_write(b"!")
		self.main.serial.flush()
		self.main._pause = True

	#----------------------------------------------------------------------
	def resume(self, event=None):
		if event is not None and not self.main.acceptKey(True): return
		if self.main.serial is None: return
		self.main.serial_write(b"~")
		self.main.serial.flush()
		self.main._msg   = None
		self.main._alarm = False
		self.main._pause = False

	#----------------------------------------------------------------------
	def pause(self, event=None):
		if self.main.serial is None: return
		if self.main._pause:
			self.main.resume()
		else:
			self.main.feedHold()

	#----------------------------------------------------------------------
	# Purge the buffer of the controller. Unfortunately we have to perform
	# a reset to clear the buffer of the controller
	#---------------------------------------------------------------------
	def purgeController(self):
		self.main.serial_write(b"!")
		self.main.serial.flush()
		time.sleep(1)
		# remember and send all G commands
		G = " ".join([x for x in CNC.vars["G"] if x[0]=="G"])	# remember $G
		TLO = CNC.vars["TLO"]
		self.softReset(False)			# reset controller
		self.purgeControllerExtra()
		self.main.runEnded()
		self.main.stopProbe()
		if G: self.main.sendGCode(G)			# restore $G
		self.main.sendGCode("G43.1Z%s"%(TLO))	# restore TLO
		self.viewState()


	#----------------------------------------------------------------------
	def parseLine(self, line, cline, sline):
		if not line:
			return True

		elif line[0]=="<":
			if not self.main.sio_status:
				self.main.log.put((self.main.MSG_RECEIVE, line))
			else:
				self.parseBracketAngle(line, cline)

		elif line[0]=="[":
			self.main.log.put((self.main.MSG_RECEIVE, line))
			self.parseBracketSquare(line)

		elif "error:" in line or "ALARM:" in line:
			self.main.log.put((self.main.MSG_ERROR, line))
			self.main._gcount += 1
			#print "gcount ERROR=",self._gcount
			if cline: del cline[0]
			if sline: CNC.vars["errline"] = sline.pop(0)
			if not self.main._alarm: self.main._posUpdate = True
			self.main._alarm = True
			CNC.vars["state"] = line
			if self.main.running:
				self.main._stop = True

		elif line.find("ok")>=0:
			self.main.log.put((self.main.MSG_OK, line))
			self.main._gcount += 1
			if cline: del cline[0]
			if sline: del sline[0]
			#print "SLINE:",sline
#			if  self._alarm and not self.running:
#				# turn off alarm for connected status once
#				# a valid gcode event occurs
#				self._alarm = False

		elif line[0] == "$":
			self.main.log.put((self.main.MSG_RECEIVE, line))
			pat = VARPAT.match(line)
			if pat:
				CNC.vars["grbl_%s"%(pat.group(1))] = pat.group(2)

		elif line[:4]=="Grbl" or line[:13]=="CarbideMotion": # and self.running:
			#tg = time.time()
			self.main.log.put((self.main.MSG_RECEIVE, line))
			self.main._stop = True
			del cline[:]	# After reset clear the buffer counters
			del sline[:]
			CNC.vars["version"] = line.split()[1]
			# Detect controller
			if self.main.controller in ("GRBL0", "GRBL1"):
				self.main.controllerSet("GRBL%d"%(int(CNC.vars["version"][0])))

		else:
			#We return false in order to tell that we can't parse this line
			#Sender will log the line in such case
			return False

		#Parsing succesfull
		return True
